import { mapGetters, mapState} from 'vuex'
export default {
  data: () => ({

    dialog: false,
    headers: [
      {
        sortable: true,
        text: 'Name Purpose',
        value: 'name'
      },
      {
        sortable:true,
        text: 'Room Owner',
        value: 'Owner.name'

      },
      {
        sortable:true,
        text: 'Room Manager',
        value: 'Manager.name'

      },
      { text: 'Actions', 
        value: 'action', 
        sortable: false 
      },
    ],
    editedDepartment: {
      name: '',
    },
    defaultDepartment: {
      name: '',
    },
  }),

  computed: {
    ...mapState({
      devices: 'devicesArray',
      roles: 'Roles',
      purposes: 'Purposes',
      departments: 'Departments'

    }),

  },
  watch : {
    devices : function() {

    }
  },
  beforeCreate() {
    this.$store.dispatch("getDepartmentsDB")
  },
  mounted () {

  },
  methods: {
    openDetail(selectedDepartment){
      this.$store.commit('changeDetailedDepartmentId', selectedDepartment.id)
      this.$router.push({ name: 'detailed-departments'})

    },
    cancel () {
      console.log("in cancel function")
      this.dialog = false
      this.editedIndex = -1
      Object.assign(this.editedDepartment, this.defaultDepartment)
    },
    save () {
      let item = Object.assign({}, this.editedDepartment)
      this.$store.dispatch('addNewDepartmenttoDB', item)
      this.cancel()
    },
    remove(item){
      let idItem = item.id
      this.$store.dispatch('removeDepartmentDB', idItem)
      this.$store.dispatch("getDepartmentsDB")
    }
  }
}
